mapper.select:
SELECT MENU_ID, MENU_NAME, MENU_URL, PARENT_ID, MENU_ORDER, MENU_TYPE FROM SYS_MENU where PARENT_ID = '0'
AND MENU_TYPE=#{menu_type}
ORDER BY MENU_ORDER ASC ,MENU_TYPE ASC

mapper.update:
UPDATE SYS_MENU SET 
	MENU_ICON=#{MENU_ICON}
WHERE MENU_ID=#{MENU_ID}

UPDATE SYS_MENU SET 
	MENU_TYPE=#{MENU_TYPE}
WHERE PARENT_ID=#{MENU_ID}

mapper.delete:
DELETE FROM SYS_MENU 
WHERE MENU_ID=#{MENU_ID} 
OR PARENT_ID=#{MENU_ID}
